mapper.insert:
    	insert 
    	  into file_info 
    	       (
				  origin_name	
				, save_name		
				, save_path		
				, content_type	
				, extension		
				, size   
    	       ) 
    	values (
				  #{origin_name	  }
				, #{save_name	  }
				, #{save_path	  }
				, #{content_type  }
				, #{extension	  }
				, #{size          }
    	       )
